<?php

$actual_num = 5;
$code = '';

$input = '



';
$lines = explode("\n", $input);

foreach ($lines as $l) {
	$chars = str_split($l);
	foreach ($chars as $ch) {
		switch ($ch) {
			case 'U':
				if ($actual_num > 3) $actual_num -= 3;
				break;
			case 'L':
				if (!in_array($actual_num, [1, 4, 7])) $actual_num -= 1;
				break;
			case 'D':
				if ($actual_num < 7) $actual_num += 3;
				break;
			case 'R':
				if (!in_array($actual_num, [3, 6, 9])) $actual_num += 1;
				break;
		}
	}
	$code .= $actual_num;
}

echo "code: $code\n";
