<?php

$actual_row = 2;
$actual_col = 0;
$code = '';

$input = '



';
$lines = explode("\n", $input);

$pinpad = [
	[0, 0, 1, 0, 0],
	[0, 2, 3, 4, 0],
	[5, 6, 7, 8, 9],
	[0, 'A', 'B', 'C', 0],
	[0, 0, 'D', 0, 0]
];

foreach ($lines as $l) {
	$chars = str_split($l);
	foreach ($chars as $ch) {
		switch ($ch) {
			case 'U':
				if ($actual_row > 0 && $pinpad[$actual_row-1][$actual_col] != '0') $actual_row -= 1;
				break;
			case 'L':
				if ($actual_col > 0 && $pinpad[$actual_row][$actual_col-1] != '0') $actual_col -= 1;
				break;
			case 'D':
				if ($actual_row < sizeof($pinpad)-1 && $pinpad[$actual_row+1][$actual_col] != '0') $actual_row += 1;
				break;
			case 'R':
				if ($actual_col < sizeof($pinpad[0])-1 && $pinpad[$actual_row][$actual_col+1] != '0') $actual_col += 1;
				break;
		}
	}
	$code .= $pinpad[$actual_row][$actual_col];
}

echo "code: $code\n";
